import { HeartIcon } from '@heroicons/react/solid';
import axios from 'axios';
import { useEffect, useState } from 'react';

function FavoriteButton({ propertyId }) {
  const [isFavorited, setIsFavorited] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const checkFavorite = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/favorites/${propertyId}`, {
          headers: { Authorization: `Bearer ${user.token}` },
        });
        setIsFavorited(response.data.isFavorited);
      } catch (err) {
        console.error('Error checking favorite:', err);
      }
    };
    if (user) checkFavorite();
  }, [propertyId]);

  const toggleFavorite = async () => {
    try {
      if (isFavorited) {
        await axios.delete(`http://localhost:5000/api/favorites/${propertyId}`, {
          headers: { Authorization: `Bearer ${user.token}` },
        });
      } else {
        await axios.post(
          'http://localhost:5000/api/favorites',
          { propertyId },
          { headers: { Authorization: `Bearer ${user.token}` } }
        );
      }
      setIsFavorited(!isFavorited);
    } catch (err) {
      console.error('Error toggling favorite:', err);
    }
  };

  if (!user) return null;

  return (
    <button onClick={toggleFavorite} className="flex items-center space-x-1">
      <HeartIcon className={`h-6 w-6 ${isFavorited ? 'text-red-500' : 'text-gray-400'}`} />
      <span className="text-sm">{isFavorited ? 'Remove Favorite' : 'Add Favorite'}</span>
    </button>
  );
}

export default FavoriteButton;
