import axios from 'axios';
import { useEffect, useState } from 'react';
import PropertyCard from '../components/PropertyCard';
import PropertyForm from '../components/PropertyForm';

function LandlordDashboard() {
  const [properties, setProperties] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/properties', {
          headers: { Authorization: `Bearer ${user.token}` },
        });
        setProperties(response.data.filter((p) => p.landlord === user.id));
      } catch (err) {
        console.error('Error fetching properties:', err);
      }
    };
    if (user) fetchProperties();
  }, []);

  const handleAddProperty = async (formData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/properties', formData, {
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setProperties([...properties, response.data]);
      setShowForm(false);
    } catch (err) {
      console.error('Error adding property:', err);
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Landlord Dashboard</h1>
      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add New Property'}
      </button>
      {showForm && <PropertyForm onSubmit={handleAddProperty} />}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {properties.map((property) => (
          <PropertyCard key={property._id} property={property} isLandlord />
        ))}
      </div>
    </div>
  );
}

export default LandlordDashboard;
