import { useNavigate } from 'react-router-dom';
import FavoriteButton from './FavoriteButton';

function PropertyCard({ property, isLandlord }) {
  const navigate = useNavigate();

  const handleContact = () => {
    // Placeholder for contact functionality
    alert('Contact landlord feature coming soon!');
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden">
      <img
        src={property.images[0] || 'https://via.placeholder.com/300x200'}
        alt={property.title}
        className="w-full h-48 object-cover"
      />
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-900">{property.title}</h3>
        <p className="text-sm text-gray-600">{property.location}</p>
        <p className="text-lg font-bold text-blue-600">â‚¹{property.price.toLocaleString()}</p>
        <p className="text-sm text-gray-600">{property.propertyType} | {property.size} sqft</p>
        <div className="flex flex-wrap gap-2 mt-2">
          {property.amenities.map((amenity) => (
            <span key={amenity} className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
              {amenity}
            </span>
          ))}
        </div>
        <div className="mt-4 flex justify-between">
          {!isLandlord && (
            <>
              <button
                onClick={handleContact}
                className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
              >
                Contact Landlord
              </button>
              <FavoriteButton propertyId={property._id} />
            </>
          )}
          {isLandlord && (
            <button
              onClick={() => navigate(`/edit-property/${property._id}`)}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Edit
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default PropertyCard;
