import { Link } from 'react-router-dom';
import FavoriteButton from './FavoriteButton';

function PropertyCard({ property, isFeatured = false }) {
  if (!property) return null; // Safety check

  const {
    _id,
    title,
    location,
    price,
    type,
    sqft,
    amenities = [],
    images = [],
  } = property;

  return (
    <div className={`bg-white rounded-lg shadow-lg overflow-hidden transform transition duration-300 hover:shadow-xl ${isFeatured ? 'border-2 border-blue-500' : ''}`}>
      {/* Property Image */}
      <div className="relative">
        <img
          src={images[0] || 'https://via.placeholder.com/400x250?text=Property+Image'}
          alt={title}
          className="w-full h-48 object-cover"
          loading="lazy"
        />
        {isFeatured && (
          <span className="absolute top-2 left-2 bg-blue-600 text-white text-xs font-semibold px-2 py-1 rounded">Featured</span>
        )}
        <div className="absolute top-2 right-2">
          <FavoriteButton propertyId={_id} />
        </div>
      </div>

      {/* Property Info */}
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-800 truncate">{title}</h3>
        <p className="text-sm text-gray-600 mt-1">{location}</p>
        <p className="text-xl font-bold text-blue-600 mt-2">₹{price.toLocaleString('en-IN')} /mo</p>

        <div className="flex items-center space-x-2 text-sm text-gray-500 mt-2">
          <span>{type}</span>
          <span>•</span>
          <span>{sqft ? `${sqft} sq.ft.` : 'N/A'}</span>
        </div>

        {/* Amenities */}
        <div className="flex flex-wrap gap-2 mt-2">
          {amenities.slice(0, 3).map((amenity) => (
            <span key={`${_id}-${amenity}`} className="bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded">
              {amenity}
            </span>
          ))}
        </div>

        {/* Verified Badge */}
        <div className="flex items-center mt-3">
          <svg className="w-4 h-4 text-green-500 mr-1" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
          </svg>
          <span className="text-sm text-gray-600">Verified</span>
        </div>

        {/* View Details Button */}
        <Link
          to={`/property/${_id}`}
          className="block mt-4 bg-blue-600 text-white text-center py-2 rounded-lg font-semibold hover:bg-blue-700 transition duration-200"
          aria-label={`View details for ${title}`}
        >
          View Details
        </Link>
      </div>
    </div>
  );
}

export default PropertyCard;
