import axios from 'axios';
import { useState } from 'react';
import PropertyCard from '../components/PropertyCard';
import SearchBar from '../components/SearchBar';

function Home() {
  const [properties, setProperties] = useState([]);

  const handleSearch = async (filters) => {
    try {
      const response = await axios.get('http://localhost:5000/api/properties', { params: filters });
      setProperties(response.data);
    } catch (err) {
      console.error('Error fetching properties:', err);
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-900">Find Your Perfect Home with Cozy Corner</h1>
        <p className="mt-4 text-lg text-gray-600">Discover the best properties across India with ease.</p>
      </div>
      <SearchBar onSearch={handleSearch} />
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-8">
        {properties.map((property) => (
          <PropertyCard key={property._id} property={property} />
        ))}
      </div>
    </div>
  );
}

export default Home;
