import { useState } from 'react';

function SearchBar({ onSearch }) {
  const [filters, setFilters] = useState({
    location: '',
    priceMin: '',
    priceMax: '',
    type: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(filters);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white rounded-lg shadow-lg p-6 flex flex-col md:flex-row gap-4 items-center"
      aria-label="Property search form"
    >
      <div className="flex-1">
        <input
          type="text"
          name="location"
          value={filters.location}
          onChange={handleChange}
          placeholder="Enter city or locality (e.g., Mumbai)"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-200"
          aria-label="Search by location"
        />
      </div>
      <div className="flex-1">
        <input
          type="number"
          name="priceMin"
          value={filters.priceMin}
          onChange={handleChange}
          placeholder="Min Price (₹)"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-200"
          aria-label="Minimum price"
        />
      </div>
      <div className="flex-1">
        <input
          type="number"
          name="priceMax"
          value={filters.priceMax}
          onChange={handleChange}
          placeholder="Max Price (₹)"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-200"
          aria-label="Maximum price"
        />
      </div>
      <div className="flex-1">
        <select
          name="type"
          value={filters.type}
          onChange={handleChange}
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-200"
          aria-label="Property type"
        >
          <option value="">Select Type</option>
          <option value="1 BHK">1 BHK</option>
          <option value="2 BHK">2 BHK</option>
          <option value="3 BHK">3 BHK</option>
          <option value="Villa">Villa</option>
        </select>
      </div>
      <button
        type="submit"
        className="bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 transition duration-200"
        aria-label="Search properties"
      >
        Search
      </button>
    </form>
  );
}

export default SearchBar;
